import React, { useState, useEffect } from "react";
import Movie from "./components/Movie";
import MovieDetails from "./components/MovieDetails";
import { fetchMovies, fetchMovieDetails } from "./services/movieService";

const App = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchMovies(searchTerm).then(setMovies);
  }, [searchTerm]);

  const selectMovie = async (movie) => {
    const details = await fetchMovieDetails(movie.id);
    setSelectedMovie({ ...movie, trailer_key: details.trailer_key });
  };

  return (
    <div className="app">
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search for a movie..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-bar"
      />

      {/* Movie Details */}
      {selectedMovie && <MovieDetails movie={selectedMovie} />}

      {/* Movie List */}
      <div className="movies-container">
        {movies.map((movie) => (
          <Movie key={movie.id} movie={movie} selectMovie={selectMovie} />
        ))}
      </div>
    </div>
  );
};

export default App;
