import { useEffect, useState } from "react";
import './App.css';
import axios from 'axios';
import Movie from "./components/Movie";
import Youtube from 'react-youtube';

function App() {
    const MOVIE_API = "https://api.themoviedb.org/3/";
    const SEARCH_API = MOVIE_API + "search/movie";
    const DISCOVER_API = MOVIE_API + "discover/movie";
    const BACKDROP_PATH = "https://image.tmdb.org/t/p/w1280";

    const API_TOKEN = "eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJlNmZjZDI1ZDU1NDc3YmIzZDVkM2U0Y2VkZmUxOWJmZSIsIm5iZiI6MTcyMjk0NzgxMi41NjYwMDAyLCJzdWIiOiI2NmIyMThlNGQyMGRlMjRhZWE5OWFhMGEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.lGBAArKNl-dgzuV_8il6y_WCNmO4JnMubI8X-PLwee0"; // Replace with your TMDb Read Access Token

    const [playing, setPlaying] = useState(false);
    const [trailer, setTrailer] = useState(null);
    const [movies, setMovies] = useState([]);
    const [searchKey, setSearchKey] = useState("");
    const [movie, setMovie] = useState({ title: "Loading Movies" });

    useEffect(() => {
        fetchMovies();
    }, []);

    const fetchMovies = async (event) => {
        if (event) event.preventDefault();

        const { data } = await axios.get(`${searchKey ? SEARCH_API : DISCOVER_API}`, {
            headers: { Authorization: `Bearer ${API_TOKEN}` },
            params: { query: searchKey }
        });

        setMovies(data.results);
        setMovie(data.results[0]);

        if (data.results.length) {
            await fetchMovie(data.results[0].id);
        }
    };

    const fetchMovie = async (id) => {
        const { data } = await axios.get(`${MOVIE_API}movie/${id}`, {
            headers: { Authorization: `Bearer ${API_TOKEN}` },
            params: { append_to_response: "videos" }
        });

        if (data.videos && data.videos.results.length) {
            const trailer = data.videos.results.find(vid => vid.name === "Official Trailer") || data.videos.results[0];
            setTrailer(trailer);
        }

        setMovie(data);
    };

    const selectMovie = (movie) => {
        fetchMovie(movie.id);
        setPlaying(false);
        setMovie(movie);
        window.scrollTo(0, 0);
    };

    const renderMovies = () => (
        movies.map(movie => (
            <Movie key={movie.id} movie={movie} selectMovie={selectMovie} />
        ))
    );

    return (
        <div className="App">
            <header className="center-max-size header">
                <span className="brand">Movie Trailer App</span>
                <form className="form" onSubmit={fetchMovies}>
                    <input className="search" type="text" id="search" onInput={(e) => setSearchKey(e.target.value)} />
                    <button className="submit-search" type="submit"><i className="fa fa-search"></i></button>
                </form>
            </header>
            {movies.length ? (
                <main>
                    {movie && (
                        <div className="poster" style={{ backgroundImage: `linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, 1)), url(${BACKDROP_PATH}${movie.backdrop_path})` }}>
                            {playing ? (
                                <>
                                    <Youtube
                                        videoId={trailer?.key}
                                        className="youtube amru"
                                        containerClassName="youtube-container amru"
                                        opts={{
                                            width: '100%',
                                            height: '100%',
                                            playerVars: {
                                                autoplay: 1,
                                                controls: 0,
                                                fs: 0,
                                                modestbranding: 0,
                                                rel: 0,
                                                showinfo: 0,
                                            },
                                        }}
                                    />
                                    <button onClick={() => setPlaying(false)} className="button close-video">Close</button>
                                </>
                            ) : (
                                <div className="center-max-size">
                                    <div className="poster-content">
                                        {trailer ? (
                                            <button className="button play-video" onClick={() => setPlaying(true)}>Play Trailer</button>
                                        ) : 'Sorry, no trailer available'}
                                        <h1>{movie.title}</h1>
                                        <p>{movie.overview}</p>
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                    <div className="center-max-size container">
                        {renderMovies()}
                    </div>
                </main>
            ) : 'Sorry, no movies found'}
        </div>
    );
}

export default App;
