const API_KEY = "e6fcd25d55477bb3d5d3e4cedfe19bfe";  
const BASE_URL = "https://api.themoviedb.org/3";

export const fetchMovies = async (searchTerm) => {
  const response = await fetch(
    `${BASE_URL}/search/movie?api_key=${API_KEY}&query=${searchTerm}`
  );
  const data = await response.json();
  return data.results;
};

export const fetchMovieDetails = async (movieId) => {
  const response = await fetch(
    `${BASE_URL}/movie/${movieId}?api_key=${API_KEY}&append_to_response=videos`
  );
  const data = await response.json();
  const trailer = data.videos.results.find((vid) => vid.type === "Trailer");
  return { ...data, trailer_key: trailer ? trailer.key : "" };
};
